mapper.update:
update funding
set ftitle = #{ftitle}, fcontent = #{fcontent}, fcreator = #{fcreator}, fcreatorInfo = #{fcreatorInfo}, fgoal = #{fgoal}, endDate = #{endDate}
where fno = #{fno}

mapper.select:
select fno, id, ftitle, freward, fcontent, fcreator, fcreatorInfo, fbacker, fgoal, fileName
from funding
where id = #{userId}
